#include <iostream>#include <string>// Function to search for the pattern in the textbool search(const std::string& txt, const std::string& pat) {    int n = txt.size();    int m = pat.size();    // Iterate through the text    for (int i = 0; i <= n - m; ++i) {        int j;        // Check if pattern matches at current position        for (j = 0; j < m && txt[i + j] == pat[j]; ++j);        // If pattern match found, return true        if (j == m) {            return true;        }    }    // Pattern not found    return false;}int main() {    std::string text = "THIS IS A TEST TEXT";    std::string pattern = "TEST";    if (search(text, pattern)) {        std::cout << "Pattern found!" << std::endl;    } else {        std::cout << "Pattern not found." << std::endl;    }    return 0;}