#include <iostream>#include <vector>#include <algorithm>using namespace std;// Function to perform Counting Sortvoid countingSort(vector<int>& arr) {    int maxElement = *max_element(arr.begin(), arr.end()); // Find the maximum element    vector<int> count(maxElement + 1, 0); // Create count array to store counts of individual elements    for (int i = 0; i < arr.size(); i++) // Store count of each element        count[arr[i]]++;    for (int i = 1; i <= maxElement; i++) // Modify count array to store cumulative counts        count[i] += count[i - 1];    vector<int> output(arr.size()); // Create output array    for (int i = arr.size() - 1; i >= 0; i--) { // Build the output array        output[count[arr[i]] - 1] = arr[i];        count[arr[i]]--;    }    for (int i = 0; i < arr.size(); i++) // Copy sorted elements back to original array        arr[i] = output[i];}int main() {    int n;    cout << "Enter the number of elements: ";    cin >> n;    vector<int> arr(n);    cout << "Enter " << n << " elements: ";    for (int i = 0; i < n; i++)        cin >> arr[i];    cout << "Original array:" << endl;    for (int i = 0; i < n; i++)        cout << arr[i] << " ";    cout << endl;    countingSort(arr);    cout << "Sorted array:" << endl;    for (int i = 0; i < n; i++)        cout << arr[i] << " ";    cout << endl;    return 0;}