#include <iostream>#include <vector>using namespace std;void heapify(vector<int>& arr, int n, int i) {    int largest = i, left = 2 * i + 1, right = 2 * i + 2;    if (left < n && arr[left] > arr[largest]) largest = left;    if (right < n && arr[right] > arr[largest]) largest = right;    if (largest != i) {        swap(arr[i], arr[largest]);        heapify(arr, n, largest);    }}void buildHeap(vector<int>& arr) {    for (int i = arr.size() / 2 - 1; i >= 0; i--) heapify(arr, arr.size(), i);}void heapSort(vector<int>& arr) {    buildHeap(arr);    for (int i = arr.size() - 1; i > 0; i--) {        swap(arr[0], arr[i]);        heapify(arr, i, 0);    }}int main() {    int n;    cout << "Enter the number of elements: ";    cin >> n;    vector<int> arr(n);    cout << "Enter " << n << " elements: ";    for (int i = 0; i < n; i++) cin >> arr[i];    heapSort(arr);    cout << "Sorted array: ";    for (int i = 0; i < n; i++) cout << arr[i] << " ";    cout << endl;    return 0;}