#include <iostream>#include <vector>#include <random>using namespace std;void heapify(vector<int>& arr, int n, int i) {    int largest = i, left = 2 * i + 1, right = 2 * i + 2;    if (left < n && arr[left] > arr[largest]) largest = left;    if (right < n && arr[right] > arr[largest]) largest = right;    if (largest != i) {        swap(arr[i], arr[largest]);        heapify(arr, n, largest);    }}void buildHeap(vector<int>& arr) {    for (int i = arr.size() / 2 - 1; i >= 0; i--) heapify(arr, arr.size(), i);}void heapSort(vector<int>& arr) {    buildHeap(arr);    for (int i = arr.size() - 1; i > 0; i--) {        swap(arr[0], arr[i]);        heapify(arr, i, 0);    }}int main() {    random_device rd;    mt19937 gen(rd());    uniform_int_distribution<> dis(1000, 5000);    int N;    cout << "Enter the number of elements (more than 200): ";    cin >> N;    vector<int> arr(N);    for (int i = 0; i < N; i++) {        arr[i] = dis(gen);    }    heapSort(arr);    cout << "Sorted array:" << endl;    for (int i = 0; i < N; i++) {        cout << arr[i] << " ";        if ((i + 1) % 10 == 0)            cout << endl;    }    cout << endl;    return 0;}