#include <iostream>#include <vector>using namespace std;#define INF 999int main() {    int V;    cout << "Enter the number of vertices: ";    cin >> V;    vector<vector<int>> G(V, vector<int>(V));    cout << "Enter the adjacency matrix (" << V << "x" << V << "):" << endl;    for (int i = 0; i < V; ++i) {        for (int j = 0; j < V; ++j) {            cin >> G[i][j];        }    }    int selected[V] = {false}; // Track selected vertices    int no_edge = 0; // Number of edges in MST    // Start with the first vertex    selected[0] = true;    cout << "Edge : Weight" << endl;    while (no_edge < V - 1) {        int min = INF;        int x, y;        // Iterate over selected vertices        for (int i = 0; i < V; i++) {            if (selected[i]) {                // Iterate over all vertices                for (int j = 0; j < V; j++) {                    if (!selected[j] && G[i][j] && G[i][j] < min) {                        min = G[i][j];                        x = i;                        y = j;                    }                }            }        }        cout << x << " - " << y << " :  " << G[x][y] << endl;        selected[y] = true;        no_edge++;    }    return 0;}